// rent.js
import React from 'react';
import { useParams } from 'react-router-dom';
import data from '../data/house.json';

const Rent = () => {
  const { id } = useParams();
  const selectedCard = data.find((card) => card.id === id);

  if (!selectedCard) {
    // Handle case where card with the specified id is not found
    return <div>Card not found</div>;
  }

  // Function to render star icons based on the rating
  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(<span key={i} className={i <= rating ? 'active' : ''}>â˜…</span>);
    }
    return stars;
  };

  return (
    <div>
      <header>
        {/* Add your header content here */}
      </header>
      <section>
        <div>
          {/* Add your lightbox with pictures here */}
        </div>
        <div>
          <h2>{selectedCard.title}</h2>
          <p>Host: {selectedCard.host.name}</p>
          <img src={selectedCard.host.picture} alt={selectedCard.host.name} />
        </div>
        <div>
          <h3>Tags</h3>
          <ul>
            {selectedCard.tags.map((tag, index) => (
              <li key={index}>{tag}</li>
            ))}
          </ul>
        </div>
        <div>
          <h3>Rating</h3>
          <div>
            {renderStars(parseInt(selectedCard.rating, 10))}
          </div>
        </div>
        <div>
          <h3>Description</h3>
          <p>{selectedCard.description}</p>
        </div>
        <div>
          <h3>Equipment</h3>
          <ul>
            {selectedCard.equipments.map((equipment, index) => (
              <li key={index}>{equipment}</li>
            ))}
          </ul>
        </div>
      </section>
    </div>
  );
};

export default Rent;
